import axios from "axios";
import { useState } from "react";

const EmpById = () => {
    const [empData, setEmpData] = useState("");

    const handleChange = (evt) => {
        setEmpData({ ...empData, [evt.target.name]: evt.target.value });
    }

    const handleSubmit = (evt) => {
        evt.preventDefault();

        console.log('submit');
        axios.get(`http://localhost:9090/emp/get-emp-by-id/${empData.employeeId}`, empData)
        .then((resp) => {
            console.log(resp);
            setEmpData(resp.data);
        })
        .catch(error => {
            console.log(error);
            throw new Error(error);   
        });
    };

    return (
        <div className="container mt-3">
            <h1>Employee by Id</h1>
            <div className="col-4">
            <form onSubmit={handleSubmit}>
                <label htmlFor="employeeId">Employee Id:</label>
                <input className="form-control" type="text" id="employeeId" name="employeeId" value={empData ? empData.employeeId : ''} onChange={handleChange} placeholder="Enter employee id" required  />
                <br/>
                <input className="form-control btn btn-outline-light"type="submit" value="get employee with id"/>
            </form>
            </div>
            <div>
                <h3>Employee</h3>
                
                    <table className="container text-left">
                        <thead className="row">
                            
                                <th className="col">Name</th> 
                                <th className="col ">Salary</th> 
                                <th className="col">Aadhar</th>
                            
                        </thead>
                        <tbody>
                        {empData &&
                            <tr className="row">
                                <td className="col">{empData.firstName}</td> 
                                <td className="col">{empData.salary}</td>
                                <td className="col">{empData.aadhar}</td>
                            </tr>
                        }

                        </tbody>
                    </table>
                
            </div>
        </div>
    );
}
 
export default EmpById;

