import axios from "axios";
import { useState } from "react";

const EmpByName = () => {
    const [empData, setEmpData] = useState("");

    const handleChange = (evt) => {
        setEmpData({ ...empData, [evt.target.name]: evt.target.value });
    }

    const handleSubmit = (evt) => {
        evt.preventDefault();

        console.log('submit');
        axios.get(`http://localhost:9090/emp/get-emp-by-name/${empData.fame}`, empData)
        .then((resp) => {
            console.log(resp);
            setEmpData(resp.data);
        })
        .catch(error => {
            console.log(error);
            throw new Error(error);   
        });
    };

    return (
        <div className="container mt-3">
            <h1>Employee by name</h1>
            <div className="col-4">
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Employee Name:</label>
                <input className="form-control" type="text" id="name" name="name" value={empData ? empData.firstName : ''} onChange={handleChange} placeholder="Enter employee name" required  />
                <br/>
                <input className="form-control btn btn-outline-light"type="submit" value="get employee by name"/>
            </form>
            </div>
            <div>
                <h3>Employee</h3>
                
                    <table className="container text-left">
                        <thead className="row">
                            
                                <th className="col">Name</th> 
                                <th className="col ">Salary</th> 
                                <th className="col">Aadhar</th>
                            
                        </thead>
                        <tbody>
                        {empData &&
                            <tr className="row">
                                <td className="col">{empData.firstName}</td> 
                                <td className="col">{empData.salary}</td>
                                <td className="col">{empData.aadhar}</td>
                            </tr>
                        }

                        </tbody>
                    </table>
                
            </div>
        </div>
    );
}
 
export default EmpByName;
