import axios from "axios";
import { useState } from "react";
const DeleteEmp = () => {

    const [empData, setEmpData] = useState({ employeeId: '' });

    const handleChange = (evt) => {
        setEmpData({ ...empData, [evt.target.name]: evt.target.value });
    }

    const handleSubmit =(evt)=>{
        evt.preventDefault();
        axios.delete(`http://localhost:9090/emp/delete-emp/${empData.employeeId}`,empData, {
            withCredentials: true,
            crossOrigin: true,
          })
        .then((resp) => {
            if(resp.status===200)
            alert(`deleted successfully!`);
            setEmpData({ employeeId:'' });
        })
        .catch(error => {
            console.error("Error deleting employee:", error);
        });

    }



    return ( 
        <div className="container mt-3">
        <h1>Delete employee </h1>
        <form onSubmit={handleSubmit}>
            <label htmlFor="employeeId">Employee Id:</label>
            <input className=""type="text" id="employeeId" name="employeeId" value={empData.employeeId} onChange={handleChange} placeholder="Enter employee id" required autoFocus />
            <br/>
            <input type="submit" value="delete employee with id"/>
        </form>
      
        </div>
     );
}
 
export default DeleteEmp;
