import React from "react";
import { useSelector } from "react-redux";
import UpdateProfile from "./UpdateProfile";

const Profile = () => {
    const userData = useSelector((state) => state.user.loggedInUser);

    return (
        <div className="d-flex flex-row justify-content-evenly" >
            <div>
            <h1>User Profile</h1>
            
            {userData && (
                // <div>
                //     <p>Username: {userData.username}</p>
                //     <p>Fist name: {userData.firstName}</p>
                //     <p>Last Name: {userData.lastName}</p>
                //     <p>Phone: {userData.phone}</p>
                //     <p>Email: {userData.email}</p>
                //     {userData.avatar && <img width={'300px'} src={userData.avatar} alt="Avatar" />}
                // </div>

                <div className="card col-4 d-flex flex-col" width={'10rem'}>
                    {userData.avatar && <img src={userData.avatar} class="card-img-top" alt="Profile Avatar"/>}
                    <div class="card-body">
                        <h5 class="card-title">{userData.username}</h5>
                        <p>Fist name: {userData.firstName}</p>
                        <p>Last Name: {userData.lastName}</p>
                        <p>Phone: {userData.phone}</p>
                        <p>Email: {userData.email}</p>
                        
                    </div>
                </div>
                
            )}
            </div>
            <div>
            <UpdateProfile />
                
            </div>

            
        </div>
    );
};

export default Profile;
