import axios from "axios";

const BASE_URL="http://localhost:9090/emp/"
const EmpService =  {
    addEmployee: (empData) => {
        return axios.post(`${BASE_URL}/add-emp`, empData)
            .then(response => response.data)
            .catch(error => {
                console.log(error);
                throw new Error(error);
            });
        },
    getEmployee: ()=>{
        return axios.get(`${BASE_URL}get-all-emps`)
        .then(response => response.data)
        .catch(error => {
            console.log(error);
            throw new Error(error);
    });
    },

    getEmpById: async(empData)=>{
        try{
            const response = await axios.get(`${BASE_URL}get-emp-by-id/${empData}`)
            return response.data;

        }catch(error){
            console.log(error);
            throw new Error(error);
        }
        

    }


}
 
export default EmpService;
